//클래스 선언
class Title extends React.Component{
    //생성자
    constructor(props){
        super(props);
    }
    
    render(){
        return(
            <div>
                <h3>총 페이지 수 : {props.pages}</h3>
                <h3>글 내용 : {props.contents}</h3>
                <hr />
            </div>
        );
    }
}

class ContentPart extends React.Component{
    //생성자
    constructor(props){
        super(props);
    }
    
    render(){
        return(
            <div>
                <h3>제목 : {props.title}</h3>
                <h3>출판사 : {props.pub}</h3>
                <h3>저자 : {props.author} </h3>
                <hr />
            </div>
            );
    }
}

