import React from "react";

class BoardHeading extends React.Component{
    //테이블 제목행 - 번호 제목 작성자
    render(){
       return(<thead> <th>번호</th> <th>제목</th> <th>작성자</th> </thead>);
    }
}

class BoardRow extends React.Component{
    //게시물 갯수만큼 테이블 행 구성
    render(board){
        const board_tr = board.map((board) => (<tr><td>{board.seq}</td><td>{board.title}</td><td>{board.writer}</td></tr>) );
        return ({board_tr});
    }
}

class BoardList extends React.Component{
    render(){
        const board = [
            {seq:1, title:"제목1", writer:"작성자1"},
            {seq:2, title:"제목2", writer:"작성자2"},
            {seq:3, title:"제목3", writer:"작성자3"},
            {seq:4, title:"제목4", writer:"작성자4"},
            {seq:5, title:"제목5", writer:"작성자5"}
        ]

        return (<table><BoardHeading /><BoardRow board={board} /></table>);
    }
}

export default BoardList;
