import React from "react";


class User extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name : this.props.name,
            address : this.props.address
        }
    }

    render(){

        const greeting = () => {
            alert(this.props.name + " 회원님은 " + this.props.address + " 지역에 삽니다.");//최초전달값
            alert(this.state.name + " 회원님은 " + this.state.address + " 지역에 삽니다.");//현재클래스 선언값
        }

        const change = () =>{
            this.setState({name:"오미선", address:"강원도 강릉시"});
        }
        
        return(
            <div>
                <button onClick={greeting}>인사</button>
                <button onClick= {change}>정보변경</button>
            </div>
        );
    }
}

export default User;
